import React from "react";
import { Col, Card, CardHeader, CardBody } from "reactstrap";
import styled from "styled-components";

const CardWrapper = styled.div`
    h3 {
        font-weight: 900;
    }
    img {
        width: 75px;D
    }
    .card {
        border: ${props => props.isActive ? "2px solid green" : "2px solid gray"};
        cursor: pointer;
        text-align: center;
    }
    .card-header {
        background: ${props => props.isActive ? "blue" : null};
        color: ${props => props.isActive ? "white" : null};
    }
    :hover {
        .card {
            border: 2px solid blue;

        }
    }
`

const DayCard = props => {
    // console.log(props);
    return (
        <Col>
            <CardWrapper onClick={props.selectDay} isActive={props.isActive}>
                <Card>
                    <CardHeader>{props.day}</CardHeader>
                    <CardBody>
                        <h3>{props.current.toFixed(1)}°</h3>
                        <p>
                            <img src={`${process.env.PUBLIC_URL}/icons/${props.icon}.png`} alt={props.description} />
                        </p>
                        <p><strong>High:</strong> {props.high}°</p>
                        <p><strong>Low:</strong> {props.low}°</p>
                        <p><strong>Precip:</strong> {props.precip}</p>
                    </CardBody>
                </Card>
            </CardWrapper>
        </Col>
    )
}

export default DayCard;
