import React from "react";
import { Col, Card, CardHeader, CardBody } from "reactstrap";
import styled from "styled-components";

const CardWrapper = styled.div`
    h3 {
        font-weight: 900;
    }
    img {
        width: 75px;D
    }
    .card {
        border: ${props => props.isActive ? "2px solid green" : "2px solid gray"};
        cursor: pointer;
        text-align: center;
    }
    .card-header {
        background: ${props => props.isActive ? "blue" : null};
        color: ${props => props.isActive ? "white" : null};
    }
    :hover {
        .card {
            border: 2px solid blue;

        }
    }
`

const DayCard = props => {
    
    return (
        <Col>
            <CardWrapper>
                <Card>
                    <CardHeader>Whats On Sale?</CardHeader>
                    <CardBody>
                        <h3>items</h3>
                    </CardBody>
                </Card>
            </CardWrapper>
        </Col>
    )
}

export default DayCard;
